query GetBoardItems($board_id: [ID!], $cursor: String, $limit: Int!) {
  boards (ids: $board_id) {
    id
    name
    items_page (limit: $limit, cursor: $cursor) {
      cursor
      items {
        id
        name
        column_values {
          id
          text
          value
        }
        subitems {
          id
          name
          column_values {
            id
            text
            value
          }
        }
      }
    }
  }
}
